// src/App.js
import React from 'react';
import { BrowserRouter as Router, Routes, Route, Link } from 'react-router-dom';

import AdminCreateCourse from './components/AdminCreateCourse';
import TutorUploadAssignment from './components/TutorUploadAssignment';
import StudentCourseView from './components/StudentCourseView';
import StudentSubmitAssignment from './components/StudentSubmitAssignment';
import CourseList from './pages/Courses/CourseList';
import CreateCourse from './pages/Courses/CreateCourse';

const App = () => {
  return (
    <Router>
      <div style={{ padding: '20px' }}>
        {/* üîó Navigation Buttons */}
        <nav style={{ marginBottom: '30px' }}>
          <Link to="/"><button>üè† Home</button></Link>{' '}
          <Link to="/create-course"><button>üÜï Create Course</button></Link>{' '}
          <Link to="/courses"><button>üìö Course List</button></Link>{' '}
          <Link to="/tutor-upload"><button>üì§ Tutor - Upload Assignment</button></Link>{' '}
          <Link to="/student-course-view"><button>üë©‚Äçüéì Student - View Courses</button></Link>{' '}
          <Link to="/student-submit"><button>üì® Student - Submit Assignment</button></Link>
        </nav>

        {/* üìå Route Setup */}
        <Routes>
          <Route path="/" element={<h1>Welcome to Course and Assignment Management</h1>} />
          <Route path="/tutor-upload" element={<TutorUploadAssignment />} />
          <Route path="/student-course-view" element={<StudentCourseView />} />
          <Route path="/student-submit" element={<StudentSubmitAssignment />} />
          <Route path="/courses" element={<CourseList />} />
          <Route path="/create-course" element={<CreateCourse />} />
        </Routes>
      </div>
    </Router>
  );
};

export default App;
