// src/App.js
import React from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';

import AdminCreateCourse from './components/AdminCreateCourse';
import TutorUploadAssignment from './components/TutorUploadAssignment';
import StudentCourseView from './components/StudentCourseView';
import StudentSubmitAssignment from './components/StudentSubmitAssignment';
import CourseList from './pages/Courses/CourseList';
import CreateCourse from './pages/Courses/CreateCourse'; // new pages go here

const App = () => {
  return (
    <Router>
      <div style={{ padding: '20px' }}>
        <nav style={{ marginBottom: '20px' }}>
          <Link to="/" style={{ marginRight: '10px' }}>Home</Link>
          <Link to="/create-course" style={{ marginRight: '10px' }}>Create Course</Link>
          <Link to="/courses">Course List</Link>
        </nav>

      <Routes>
        {/* Old components - temp testing */}
        <Route path="/" element={<h1>Welcome to Course and Assignment Management</h1>} />
        <Route path="/admin-old-create" element={<AdminCreateCourse />} />
        <Route path="/tutor-upload" element={<TutorUploadAssignment />} />
        <Route path="/student-course-view" element={<StudentCourseView />} />
        <Route path="/student-submit" element={<StudentSubmitAssignment />} />
        <Route path="/courses" element={<CourseList />} />

        {/* âœ… New page-based routing */}
        <Route path="/create-course" element={<CreateCourse />} />
        {/* Add more like <Route path="/courses/:id" element={<CourseDetails />} /> later */}
      </Routes>
      </div>
    </Router>
  );
};

export default App;
